{"changed":true,"filter":false,"title":"bugs_controller.rb","tooltip":"/app/controllers/bugs_controller.rb","value":"# Importando as bibliotecas\nrequire 'slack-notifier'\n\nclass BugsController < ApplicationController\n  before_action :set_bug, only: [:show, :edit, :update, :destroy]\n  \n  # GET /bugs\n  # GET /bugs.json\n  def index\n    @bugs = Bug.all\n  end\n\n  # GET /bugs/1\n  # GET /bugs/1.json\n  def show\n  end\n\n  # GET /bugs/new\n  def new\n    @bug = Bug.new\n    \n  end\n\n  # GET /bugs/1/edit\n  def edit\n  end\n\n  # POST /bugs\n  # POST /bugs.json\n  def create\n    @bug = Bug.new(bug_params)\n\n    respond_to do |format|\n      if @bug.save\n        format.html { redirect_to @bug, notice: 'Bug was successfully created.' }\n        format.json { render :show, status: :created, location: @bug }\n        # Slack Notificações de Criação\n        notifier = Slack::Notifier.new \"https://hooks.slack.com/services/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\"\n        notifier.ping \"Novo Bug cadastrado para o projeto: <https://b8d8614146d7411495dd02459d4545b0.vfs.cloud9.us-east-2.amazonaws.com/bugs/6|#{@bug.projeto.titulo}>\"\n      else\n        format.html { render :new }\n        format.json { render json: @bug.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # PATCH/PUT /bugs/1\n  # PATCH/PUT /bugs/1.json\n  def update\n    respond_to do |format|\n      if @bug.update(bug_params)\n        format.html { redirect_to @bug, notice: 'Bug was successfully updated.' }\n        format.json { render :show, status: :ok, location: @bug }\n        # Slack Notificações de Atualização\n        notifier = Slack::Notifier.new \"https://hooks.slack.com/services/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\"\n        notifier.ping \"Bug atualizado: #{@bug.titulo}\"\n      else\n        format.html { render :edit }\n        format.json { render json: @bug.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # DELETE /bugs/1\n  # DELETE /bugs/1.json\n  def destroy\n    @bug.destroy\n    respond_to do |format|\n      format.html { redirect_to bugs_url, notice: 'Bug was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_bug\n      @bug = Bug.find(params[:id])\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def bug_params\n      params.require(:bug).permit(:projeto_id, :titulo, :versao_bug_detectado, :descricao, :status)\n    end\n    \nend\n","undoManager":{"mark":68,"position":100,"stack":[[{"start":{"row":55,"column":28},"end":{"row":55,"column":29},"action":"insert","lines":["t"],"id":273}],[{"start":{"row":55,"column":29},"end":{"row":55,"column":30},"action":"insert","lines":["u"],"id":274}],[{"start":{"row":55,"column":30},"end":{"row":55,"column":31},"action":"insert","lines":["a"],"id":275}],[{"start":{"row":55,"column":31},"end":{"row":55,"column":32},"action":"insert","lines":["l"],"id":276}],[{"start":{"row":55,"column":32},"end":{"row":55,"column":33},"action":"insert","lines":["i"],"id":277}],[{"start":{"row":55,"column":33},"end":{"row":55,"column":34},"action":"insert","lines":["z"],"id":278}],[{"start":{"row":55,"column":34},"end":{"row":55,"column":35},"action":"insert","lines":["a"],"id":279}],[{"start":{"row":55,"column":35},"end":{"row":55,"column":36},"action":"insert","lines":["d"],"id":280}],[{"start":{"row":55,"column":36},"end":{"row":55,"column":37},"action":"insert","lines":["o"],"id":281}],[{"start":{"row":55,"column":46},"end":{"row":55,"column":53},"action":"remove","lines":["projeto"],"id":282},{"start":{"row":55,"column":46},"end":{"row":55,"column":47},"action":"insert","lines":["b"]}],[{"start":{"row":55,"column":47},"end":{"row":55,"column":48},"action":"insert","lines":["u"],"id":283}],[{"start":{"row":55,"column":48},"end":{"row":55,"column":49},"action":"insert","l